mapper.select:
select user_id, name, password, cell_phone, email,
		address, grade, reg_date, update_date from (
	select row_number() over (order by user_id) as seq,
		user_id, name, password, cell_phone, email, 
		address, grade, reg_date, update_date from members
) where seq between #{start} and #{end}

mapper.delete:
update members set
	password=#{newPassword}
where user_id=#{userId} and password=#{oldPassword}
